import React, { useState } from 'react';
import { Button, Form, FormControl, Row, Col } from 'react-bootstrap';
import './offres.css'; // Assurez-vous que ce chemin est correct

const AjoutOffre = ({ onAddOffer, onClose }) => {
    const [offerName, setOfferName] = useState("");

    const submitHandler = (event) => {
        event.preventDefault();
        if (!offerName) return;
        onAddOffer(offerName);
        setOfferName("");
        onClose();
    };

    return (
        <div className="form-container">
            <Form onSubmit={submitHandler} className="add-offer-form">
                <Row>
                    <Col md={8}>
                        <Form.Group controlId="offerName">
                            <Form.Label>Nom de l'offre :</Form.Label>
                            <FormControl
                                type="text"
                                value={offerName}
                                onChange={(e) => setOfferName(e.target.value)}
                                placeholder="Entrez le nom de l'offre"
                            />
                        </Form.Group>
                    </Col>
                    <Col md={4} className="mt-4 pt-2">  {/* Alignement vertical avec l'input */}
                        <Button variant="primary" type="submit">Ajouter</Button>
                        <Button variant="secondary" onClick={onClose} className="ms-2">Annuler</Button>
                    </Col>
                </Row>
            </Form>
        </div>
    );
};

export default AjoutOffre;
