import React, { useState } from "react";
import { Button, Container, Row, Col, Table, Toast, Card, Form, FormControl } from "react-bootstrap";

const Options = () => {
  const [options, setOptions] = useState(
    );
  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedOptionId, setSelectedOptionId] = useState(null);
  const [newOptionName, setNewOptionName] = useState("");

  const handleDeleteClick = (optionId) => {
    setSelectedOptionId(optionId);
    setShowToast(true);
  };

  const confirmDeletion = () => {
    setOptions(options => options.filter(option => option.id !== selectedOptionId));
    setShowToast(false);
  };

  const handleAddOption = (e) => {
    e.preventDefault();
    const newId = options.length > 0 ? Math.max(...options.map(o => o.id)) + 1 : 1;
    const newOption = {
      id: newId,
      name: newOptionName,
      categories: [] // Ajustez en fonction de la manière dont vous gérez les catégories
    };
    setOptions(prevOptions => [...prevOptions, newOption]);
    setNewOptionName("");
    setShowForm(false);
  };

  return (
    <Container>
      <div className="py-4">
        <Button onClick={() => setShowForm(true)}>Ajouter Option</Button>

        {showForm && (
          <Card className="my-4">
            <Card.Body>
              <Form onSubmit={handleAddOption}>
                <Row className="align-items-center">
                  <Col xs={12} md={8}>
                    <FormControl
                      type="text"
                      placeholder="Entrez le nom de l'option"
                      value={newOptionName}
                      onChange={(e) => setNewOptionName(e.target.value)}
                    />
                  </Col>
                  <Col xs={6} md={2}>
                    <Button variant="primary" type="submit" className="w-100">
                      Ajouter
                    </Button>
                  </Col>
                  <Col xs={6} md={2}>
                    <Button variant="secondary" onClick={() => setShowForm(false)} className="w-100">
                      Annuler
                    </Button>
                  </Col>
                </Row>
              </Form>
            </Card.Body>
          </Card>
        )}

        <Table bordered hover className="center">
          {/* Table Head */}
          <thead>
            {/* ... */}
          </thead>
          <tbody>
            {options.map((option) => (
              <tr key={option.id}>
                <td>{option.id}</td>
                <td>{option.name}</td>
                <td>{option.categories.join(", ")}</td>
                <td>
                  <Button variant="primary" className="mx-2">
                    View
                  </Button>
                  <Button variant="outline-primary" className="mx-2">
                    Edit
                  </Button>
                  <Button variant="danger" className="mx-2" onClick={() => handleDeleteClick(option.id)}>
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>

        {showToast && (
          <Toast show={showToast} onClose={() => setShowToast(false)} className="custom-toast-style" bg="light">
            <Toast.Body>
              Êtes-vous sûr de vouloir supprimer cette option ?
              <div className="mt-2 d-flex justify-content-between">
                <Button variant="danger" size="sm" onClick={confirmDeletion}>
                  Confirmer
                </Button>
                <Button variant="secondary" size="sm" onClick={() => setShowToast(false)}>
                  Annuler
                </Button>
              </div>
            </Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};

export default Options;
