const AjoutOffre = ({ onAddOffer, onClose }) => {
  const [offerName, setOfferName] = useState("");

  const submitHandler = (event) => {
    event.preventDefault();
    if (!offerName) return;
    onAddOffer(offerName); // Envoyez seulement le nom de l'offre à addOffer
    setOfferName("");
    onClose(); // Fermez le formulaire après soumission
  };

  return (
    <Form onSubmit={submitHandler}>
      <Form.Group>
        <Form.Label htmlFor="offerName">Nom de l'offre :</Form.Label>
        <FormControl
          id="offerName"
          type="text"
          value={offerName}
          onChange={(e) => setOfferName(e.target.value)}
          placeholder="Entrez le nom de l'offre"
        />
      </Form.Group>
      <Button variant="primary" type="submit">Ajouter</Button>
      <Button variant="secondary" onClick={onClose} className="ms-2">
        Annuler
      </Button>
    </Form>
  );
};

export default AjoutOffre;
