import React, { useState } from "react";
import { Button, Container, Toast, Table, Form, FormControl, Row, Col, Card } from "react-bootstrap";

const Options = () => {
  // Votre état initial et les fonctions
  // ...
  const [options, setOptions] = useState([
    {
      id: 1,
      name: "Climatisé",
      categories: ["Maison", "Appartement", "Studio"],
    },
    {
      id: 2,
      name: "chauffé",
      categories: ["Maison", "Bureau"],
    },
    {
      id: 3,
      name: "vue sur mer",
      categories: ["Maison", "Villa"],
    },
  ]);

  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedOptionId, setSelectedOptionId] = useState(null);

  const handleDeleteClick = (optionId) => {
    setSelectedOptionId(optionId);
    setShowToast(true);
  };

  // Logique pour supprimer une option
  const confirmDeletion = () => {
    setOffers((options) =>
      options.filter((option) => option.id !== selectedOptionId)
    );
    setShowToast(false);
    setSelectedOptionId(null);
  };

  const addOption = (optionName) => {
    // Trouvez le plus grand ID actuel et ajoutez 1 pour le nouvel ID
    const newId =
      options.length > 0 ? Math.max(...options.map((option) => option.id)) + 1 : 1;
    const newOption = {
      id: newId,
      name: optionName,
    };
  }
  setOptions((prevOptions) => [...prevOptions, newOption]);
 

  return (
    <Container>
      <div className="py-4">
        <Button onClick={() => setShowForm(true)}>Ajouter Option</Button>
        {showForm && <OptionForm />}
        <Table bordered hover className="custom-table text-center">
          {/* En-têtes et corps de la table comme ci-dessus */}
        </Table>
        {showToast && <DeletionToast />}
      </div>
    </Container>
  );
};

export default Options;
