import React, { useState } from "react";
import { Table, Button, Container, Toast } from "react-bootstrap";
import "./offres.css"; // Assurez-vous que le fichier CSS est correctement lié
import AjoutOffre from "./AjoutOffre"; // Importez le composant de formulaire correctement renommé
 n,b
const Offres = () => {
  const [offers, setOffers] = useState([
    { id: 1, name: "À Vendre" },
    { id: 2, name: "À Louer" },
  ]);
  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedOfferId, setSelectedOfferId] = useState(null);

  const handleDeleteClick = (offerId) => {
    setSelectedOfferId(offerId);
    setShowToast(true);
  };

  const confirmDeletion = () => {
    setOffers((offers) =>
      offers.filter((offer) => offer.id !== selectedOfferId)
    );
    setShowToast(false);
    setSelectedOfferId(null);
  };

  const addOffer = (offerName) => {
    // Trouvez le plus grand ID actuel et ajoutez 1 pour le nouvel ID
    const newId =
      offers.length > 0 ? Math.max(...offers.map((offer) => offer.id)) + 1 : 1;
    const newOffer = {
      id: newId,
      name: offerName,
    };
    // Ajoutez la nouvelle offre à la fin du tableau pour qu'elle apparaisse en dessous des autres
    setOffers((prevOffers) => [...prevOffers, newOffer]);
  };

  return (
    <Container>
      <div className="py-4">
        <Button onClick={() => setShowForm(true)}>Ajouter Offre</Button>
        {showForm && (
          <AjoutOffre // Utilisez ici le nouveau nom du composant
            onAddOffer={addOffer}
            onClose={() => setShowForm(false)}
          />
        )}
        <Table bordered hover className="custom-table text-center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom de l'offre</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {offers.map((offer) => (
              <tr key={offer.id}>
                <th scope="row">{offer.id}</th>
                <td>{offer.name}</td>
                <td>
                  <Button
                    variant="danger"
                    className="mx-2"
                    onClick={() => handleDeleteClick(offer.id)}
                  >
                    Supprimer
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {showToast && (
          <Toast
            onClose={() => setShowToast(false)}
            show={showToast}
            className="custom-toast-style"
            bg="light"
          >
            <Toast.Body>
              Êtes-vous sûr de vouloir supprimer cette offre ?
              <div className="mt-2 d-flex justify-content-between">
                <Button variant="danger" size="sm" onClick={confirmDeletion}>
                  Confirmer
                </Button>
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={() => setShowToast(false)}
                >
                  Annuler
                </Button>
              </div>
            </Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};

export default Offres;
