import React, { useEffect, useState } from "react";
import axios from "axios";
import AddUser from "./AddUser";
const Users = () => {
  const [users, setUsers] = useState([]);
  useEffect(() => {
    loadUsers();
  }, []);

  useEffect(() => {
    console.log("Modal open state has changed:", isModalOpen);  // Ce log affichera le nouvel état chaque fois qu'il change
  }, [isModalOpen]);

  const [isModalOpen, setModalOpen] = useState(false);
  const loadUsers = async () => {
    const result = await axios.get("http://localhost:8082/api/user");
    setUsers(result.data);
  };
  return (
    <div className="container">
      <button
        className="btn btn-secondary btn-lg"
        onClick={() => {
          console.log("Attempting to open modal");
          setModalOpen(true);
          console.log("Modal should be open:", isModalOpen);
        }}
      >
        Add User
      </button>
      <AddUser isOpen={isModalOpen} onClose={() => setModalOpen(false)} />
      <div className="py-4">
        <table className="table border shadow">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">First Name</th>
              <th scope="col">Email</th>
              <th scope="col">Phone Number</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr>
                <th scope="row" key={index}>
                  {index + 1}
                </th>
                <td>{user.firstName}</td>
                <td>{user.email}</td>
                <td>@{user.phoneNumber}</td>
                <td>
                  <button className="btn btn-primary mx-2">View</button>
                  <button className="btn btn-outline-primary mx-2">Edit</button>
                  <button className="btn btn-danger mx-2">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Users;
