import React, { useState } from "react";
import { Table, Button, Container, Toast } from "react-bootstrap";
import "./offres.css"; // Make sure your CSS file is linked correctly

const Offres = () => {
  const [offers, setOffers] = useState([
    { id: 1, name: "A Vendre" },
    { id: 2, name: "A Louer" },
  ]);
  const [showToast, setShowToast] = useState(false);
  const [selectedOfferId, setSelectedOfferId] = useState(null);

  const handleDeleteClick = (offerId) => {
    setShowToast(true);
    setSelectedOfferId(offerId);
  };

  const confirmDeletion = () => {
    if (selectedOfferId !== null) {
      setOffers(offers.filter((offer) => offer.id !== selectedOfferId));
    }
    setShowToast(false);
    setSelectedOfferId(null);
  };

  const cancelDeletion = () => {
    setShowToast(false);
    setSelectedOfferId(null);
  };

  return (
    <Container>
      <div className="py-4">
        <button className="mb-3">Ajouter Offre</button>  {/* Adjusted for better accessibility */}
        <Table bordered hover className="custom-table text-center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom Offre</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {offers.map((offer) => (
              <tr key={offer.id}>
                <th scope="row">{offer.id}</th>
                <td>{offer.name}</td>
                <td>
                  <Button
                    variant="danger"
                    className="mx-2"
                    onClick={() => handleDeleteClick(offer.id)}
                  >
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {showToast && (
          <div className="custom-toast">
            <Toast
              onClose={cancelDeletion}
              show={showToast}
              className="custom-toast-style"
              bg="light"
            >
              <Toast.Body>
                Are you sure you want to delete this offer?
                <div className="mt-2 d-flex justify-content-between">
                  <Button variant="danger" size="sm" onClick={confirmDeletion}>
                    Confirm
                  </Button>
                  <Button variant="secondary" size="sm" onClick={cancelDeletion}>
                    Cancel
                  </Button>
                </div>
              </Toast.Body>
            </Toast>
          </div>
        )}
      </div>
    </Container>
  );
};

export default Offres;
