import React, { useState } from 'react';
import { Button, Form, FormControl, Row, Col, Card } from 'react-bootstrap';

const AjoutOffre = ({ onAddOffer, onClose }) => {
    const [offerName, setOfferName] = useState("");

    const submitHandler = (event) => {
        event.preventDefault();
        if (!offerName) return;
        onAddOffer(offerName);
        setOfferName("");
        onClose();
    };

    return (
        <Card className="my-4">
            <Card.Body>
                <Form onSubmit={submitHandler}>
                    <Row className="align-items-center">
                        <Col xs={12} md={6}>
                            <Form.Group controlId="offerName">
                                <FormControl
                                    type="text"
                                    placeholder="Entrez le nom de l'offre"
                                    value={offerName}
                                    onChange={(e) => setOfferName(e.target.value)}
                                />
                            </Form.Group>
                        </Col>
                        <Col xs={6} md={3}>
                            <Button variant="primary" type="submit" className="w-100">
                                Ajouter
                            </Button>
                        </Col>
                        <Col xs={6} md={3}>
                            <Button variant="secondary" onClick={onClose} className="w-100">
                                Annuler
                            </Button>
                        </Col>
                    </Row>
                </Form>
            </Card.Body>
        </Card>
    );
};

export default AjoutOffre;
