import React from "react";
import { Table, Button, Container } from "react-bootstrap";

const Options = () => {
  // Example data representing options and their associated categories
  const options = [
    {
      id: 1,
      name: "Climatisé",
      categories: ["Maison", "Appartement", "Studio"]
    },
    {
      id: 2,
      name: "chauffé",
      categories: ["Maison", "Bureau"]
    },
    {
      id: 3,
      name: "vue sur mer",
      categories: ["Maison", "Villa"]
    }
  ];

  return (
    <Container>
      <div className="py-4">
        <Table bordered hover className="center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom de l'Option</th>
              <th>Categories Associées</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {options.map((option) => (
              <tr key={option.id}>
                <td>{option.id}</td>
                <td>{option.name}</td>
                <td>{option.categories.join(", ")}</td> {/* Display categories as a comma-separated list */}
                <td>
                  <Button variant="primary" className="mx-2">View</Button>
                  <Button variant="outline-primary" className="mx-2">Edit</Button>
                  <Button variant="danger" className="mx-2">Delete</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    </Container>
  );
};

export default Option
