import React, { useState } from "react";
import { Button, Row, Col, Card, Form } from "react-bootstrap";
import Select from "react-select";

const AjoutVille = ({ onAddVille, onClose }) => {
  const [villeName, setVilleName] = useState("");
  const [selectedRegions,setSelectedRegions] = useState([]);
  const regions = [
    { value: "Sousse", label: "Sousse" },
    { value: "Tunis", label: "Tunis" },
    { value: "Gafsa", label: "Gafsa" },
    { value: "Sfax", label: "Sfax" },
  ];

  const submitHandler = (event) => {
    event.preventDefault();
    if (!villeName || selectedRegions.length === 0) return;
    onAddVille(
    villeName,
      selectedRegions.map((region) => region.value)
    );
    setre("");
    setSelectedRegions([]);
    onClose();
  };

  const handleCategoryChange = (selectedVilles) => {
    setSelectedRegions(selectedVilles || []);
  };

  

  return (
    <Card className="my-4">
      <Card.Body>
        <Form onSubmit={submitHandler}>
          <Form.Group as={Row} controlId="ville">
            <Form.Label column sm={3}>
              Nom Ville:
            </Form.Label>
            <Col sm={6} className="mb-3">
              {" "}
              {/* mb-3 adds margin bottom */}
              <Form.Control
                type="text"
                placeholder="Entrez le nom de la ville"
                value={villeName}
                onChange={(e) => setre(e.target.value)}
              />
            </Col>
            <Col sm={3} />{" "}
            {/* Colonne vide pour pousser le contenu vers la gauche */}
          </Form.Group>
          <Form.Group as={Row} controlId="categorySelect">
            <Form.Label column sm={3}>
              Catégories Associées:
            </Form.Label>
            <Col sm={6}>
              <Select
                isMulti
                options={categories}
                value={selectedCategories}
                onChange={handleCategoryChange}
                className="basic-multi-select"
                classNamePrefix="select"
              />
            </Col>
            <Col sm={3} />{" "}
            {/* Colonne vide pour pousser le contenu vers la gauche */}
          </Form.Group>
          <Row>
            <Col sm={12} className="text-center">
              <Button variant="primary" type="submit" className="m-2">
                Ajouter
              </Button>
              <Button variant="secondary" onClick={onClose} className="m-2">
                Annuler
              </Button>
            </Col>
          </Row>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default AjoutVille;
