import React, { useState } from "react";
import { Table, Button, Container } from "react-bootstrap";

const Villes = () => {
  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedVilleId, setselectedVilleId] = useState(null);
  // Sample static data, replace with dynamic data fetching if necessary
  const cities = [
    {
      id: 1,
      name: "Hammam Sousse",
      region: "Sousse",
    },
    {
      id: 2,
      name: "La Marsa",
      region: "Tunis",
    },
    {
      id: 3,
      name: "Sfax",
      region: "Sfax",
    },
  ];
  const handleDeleteClick = (optionId) => {
    setselectedVilleId(optionId);
    setShowToast(true);
  };

  const confirmDeletion = () => {
    setOptions((currentOptions) =>
      currentOptions.filter((option) => option.id !== setselectedVilleId)
    );
    setShowToast(false);
    setselectedVilleId(null);
  };

  return (
    <Container>
      <div className="py-4">
        <Table bordered hover className="center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom Ville</th>
              <th>Region</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {cities.map((city) => (
              <tr key={city.id}>
                <th scope="row">{city.id}</th>
                <td>{city.name}</td>
                <td>{city.region}</td>
                <td>
                  <Button variant="primary" className="mx-2">
                    View
                  </Button>
                  <Button variant="outline-primary" className="mx-2">
                    Edit
                  </Button>
                  <Button
                    variant="danger"
                    onClick={() => handleDeleteClick(option.id)}
                  >
                    Supprimer
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    </Container>
  );
};

export default Villes;
