import React, { useState } from "react";
import { Button, Container, Toast, Table } from "react-bootstrap";
import AjoutVille from "./AjoutVille";

const Villes = () => {
  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedVilleId, setselectedVilleId] = useState(null);
  // Sample static data, replace with dynamic data fetching if necessary
  const [vi, setOptions] = useState([
    {
      id: 1,
      name: "Hammam Sousse",
      region: "Sousse",
    },
    {
      id: 2,
      name: "La Marsa",
      region: "Tunis",
    },
    {
      id: 3,
      name: "Sfax",
      region: "Sfax",
    },
  ]);
  const handleDeleteClick = (optionId) => {
    setselectedVilleId(optionId);
    setShowToast(true);
  };

  const confirmDeletion = () => {
    setOptions((currentOptions) =>
      currentOptions.filter((option) => option.id !== selectedVilleId)
    );
    setShowToast(false);
    setselectedVilleId(null);
  };

  const addOption = (optionName, region = []) => {
    // Assume categories are optionally passed
    const newId =
      options.length > 0 ? Math.max(...options.map((o) => o.id)) + 1 : 1;
    setOptions([...options, { id: newId, name: optionName, region }]);
  };

  return (
    <Container>
      <div className="py-4">
        <Button
          className="btn-primary-custom "
          onClick={() => setShowForm(true)}
        >
          Ajouter Option
        </Button>
        {showForm && (
          <AjoutVille
            onAddOption={addOption}
            onClose={() => setShowForm(false)}
          />
        )}
        <Table bordered hover className="center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom Ville</th>
              <th>Region</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {options.map((city) => (
              <tr key={city.id}>
                <th scope="row">{city.id}</th>
                <td>{city.name}</td>
                <td>{city.region}</td>
                <td>
                  <Button variant="primary" className="mx-2">
                    View
                  </Button>
                  <Button variant="outline-primary" className="mx-2">
                    Edit
                  </Button>
                  <Button
                    variant="danger"
                    onClick={() => handleDeleteClick(city.id)}
                  >
                    Supprimer
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {showToast && (
          <Toast
            onClose={() => setShowToast(false)}
            show={showToast}
            className="custom-toast-style"
            bg="light"
          >
            <Toast.Body>
              Êtes-vous sûr de vouloir supprimer cette option ?
              <div className="mt-2 d-flex justify-content-between">
                <Button variant="danger" onClick={confirmDeletion}>
                  Confirmer
                </Button>
                <Button variant="secondary" onClick={() => setShowToast(false)}>
                  Annuler
                </Button>
              </div>
            </Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};

export default Villes;
