      <Card.Body>
        <Form onSubmit={(e) => {
          e.preventDefault();
          addOption(newOptionName);
        }}>
          <Row className="align-items-center">
            {/* Contenu du formulaire comme dans AjoutOffre */}
          </Row>
        </Form>
