      // ... le reste des propriétés du toast
    >
      {/* Corps du toast */}
    </Toast>
  );

  return (
    <Container>
      <div className="py-4">
        <Button onClick={() => setShowForm(true)}>Ajouter Option</Button>
        {showForm && <OptionForm />}
        <Table bordered hover className="custom-table text-center">
          {/* En-têtes et corps de la table comme ci-dessus */}
        </Table>
        {showToast && <DeletionToast />}
      </div>
    </Container>
  );
};

export default Options;
