import React, { useState } from "react";
import { Table, Button, Container } from "react-bootstrap";

const Categories = () => {
  const [categories, setCategories] = useState([
    {
      id: 1,
      name: "Maison",
      options: ["Climatisé", "chauffé", "vue sur mer"],
    },
    {
      id: 2,
      name: "Appartement",
      options: ["Ascenseur", "Parking", "Balcon"],
    },
    {
      id: 3,
      name: "Studio",
      options: ["Meublé", "Centre ville"],
    },
  ]);

  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedCategorieId, setSelectedCategorieId] = useState(null);

  const handleDeleteClick = (categorieId) => {
    setSelectedCategorieId(categorieId);
    setShowToast(true);
  };

  const confirmDeletion = () => {
    setCategories((currentCategories) =>
      currentCategories.filter(
        (categorie) => categorie.id !== selectedCategorieId
      )
    );
    setShowToast(false);
    setSelectedCategorieId(null);
  };
  const addCategorie = (categorieName) => {
    const newId =
      offers.length > 0
        ? Math.max(...categories.map((categorie) => categorie.id)) + 1
        : 1;
    const newCategorie = {
      id: newId,
      name: categorieName,
    };
    // Ajoutez la nouvelle offre à la fin du tableau pour qu'elle apparaisse en dessous des autres
    setCategories((prevCategories) => [...prevCategories, newCategorie]);
  };
  return (
    <Container>
      <div className="py-4">
        <Button
          className="btn-primary-custom "
          onClick={() => setShowForm(true)}
        >
          Ajouter Option
        </Button>
        {showForm && (
          <AjoutOption
            onAddOption={addOption}
            onClose={() => setShowForm(false)}
          />
        )}
        <Table bordered hover className="custom-table text-center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom de l'option</th>
              <th>Catégories Associées</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {options.map((option) => (
              <tr key={option.id}>
                <td>{option.id}</td>
                <td>{option.name}</td>
                <td>
                  <ul>
                    {option.categories.map((category, index) => (
                      <li key={index}>{category}</li>
                    ))}
                  </ul>
                </td>
                <td className="align-middle">
                  <Button
                    variant="danger"
                    onClick={() => handleDeleteClick(option.id)}
                  >
                    Supprimer
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {showToast && (
          <Toast
            onClose={() => setShowToast(false)}
            show={showToast}
            className="custom-toast-style"
            bg="light"
          >
            <Toast.Body>
              Êtes-vous sûr de vouloir supprimer cette option ?
              <div className="mt-2 d-flex justify-content-between">
                <Button variant="danger" onClick={confirmDeletion}>
                  Confirmer
                </Button>
                <Button variant="secondary" onClick={() => setShowToast(false)}>
                  Annuler
                </Button>
              </div>
            </Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};

export default Categories;
