import React, { useState } from "react";
import {
  Button,
  Container,
  Row,
  Col,
  Table,
  Toast,
  Card,
  Form,
  FormControl,
} from "react-bootstrap";

const Options = () => {
  const [options, setOptions] = useState([
    {
      id: 1,
      name: "Climatisé",
      categories: ["Maison", "Appartement", "Studio"],
    },
    {
      id: 2,
      name: "chauffé",
      categories: ["Maison", "Bureau"],
    },
    {
      id: 3,
      name: "vue sur mer",
      categories: ["Maison", "Villa"],
    },
  ]);
  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedOptionId, setSelectedOptionId] = useState(null);
  const [newOptionName, setNewOptionName] = useState("");

  const handleDeleteOption = (optionId) => {
    setSelectedOptionId(optionId);
    setShowToast(true);
  };

  const confirmDeletion = () => {
    setOptions((options) =>
      options.filter((option) => option.id !== selectedOptionId)
    );
    setShowToast(false);
  };

  const addOption = (optionName) => {
    const newId =
      options.length > 0 ? Math.max(...options.map((o) => o.id)) + 1 : 1;
    setOptions([...options, { id: newId, name: optionName, categories: [] }]);
  };

  return (
    <Container>
      <div className="py-4">
        <Button onClick={() => setShowForm(true)}>Ajouter Option</Button>
        {showForm && (
          <Card className="my-4">
            <Card.Body>
              <Form
                onSubmit={(e) => {
                  e.preventDefault();
                  addOption(newOptionName);
                  setNewOptionName("");
                  setShowForm(false);
                }}
              >
                {/* Le reste du formulaire d'ajout ici */}
              </Form>
            </Card.Body>
          </Card>
        )}

        {/* La table des options ici */}

        {showToast && (
          <Toast
            show={showToast}
            onClose={() => setShowToast(false)}
            className="custom-toast-style"
            bg="light"
          >
            <Toast.Body>
              Êtes-vous sûr de vouloir supprimer cette option ?
              <div className="mt-2 d-flex justify-content-between">
                <Button variant="danger" size="sm" onClick={confirmDeletion}>
                  Confirmer
                </Button>
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={() => setShowToast(false)}
                >
                  Annuler
                </Button>
              </div>
            </Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};

export default Options;
