import React, { useState } from "react";
import { Button, Form, FormControl, Row, Col, Card } from "react-bootstrap";

const AjoutCategorie = ({ onAddCategorie, onClose }) => {
  const [categorieName, setCategorieName] = useState("");

  const submitHandler = (event) => {
    event.preventDefault();
    if (!categorieName) return;
    const cc = onAddCategorie(categorieName);
    
    setCategorieName("");
    onClose();
  };

  return (
    <Card className="my-4">
      <Card.Body>
        <Form onSubmit={submitHandler}>
        <Form.Label className="fw-bold">Nom de categorie:</Form.Label>
          <Row className="align-items-center">
            <Col xs={12} md={6}>
              <Form.Group controlId="categorieName">
                <FormControl
                  type="text"
                  placeholder="Entrez le nom de categorie"
                  value={categorieName}
                  onChange={(e) => setCategorieName(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col xs={6} md={3}>
              <Button variant="primary" type="submit" className="w-100">
                Ajouter
              </Button>
            </Col>
            <Col xs={6} md={3}>
              <Button variant="secondary" onClick={onClose} className="w-100">
                Annuler
              </Button>
            </Col>
          </Row>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default AjoutCategorie;
