import React, { useState } from "react";
import { Button, Container, Toast, Table } from "react-bootstrap";
import AjoutVille from "./AjoutVille";

const Villes = () => {
 
  // Sample static data, replace with dynamic data fetching if necessary
  const [villes, setVilles] = useState([
    {
      id: 1,
      name: "Hammam Sousse",
      region: "Sousse",
    },
    {
      id: 2,
      name: "La Marsa",
      region: "Tunis",
    },
    {
      id: 3,
      name: "Sfax",
      region: "Sfax",
    },
  ]);
  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedVilleId, setselectedVilleId] = useState(null);
  const handleDeleteClick = (villeId) => {
    setselectedVilleId(villeId);
    setShowToast(true);
  };

  const confirmDeletion = () => {
    setVilles((currentvilles) =>
      currentvilles.filter((ville) => ville.id !== selectedVilleId)
    );
    setShowToast(false);
    setselectedVilleId(null);
  };

  const addVille = (optionName, region = []) => {
    // Assume categories are optionally passed
    const newId =
      villes.length > 0 ? Math.max(...villes.map((o) => o.id)) + 1 : 1;
    setVilles([...villes, { id: newId, name: optionName, region }]);
  };

  return (
    <Container>
      <div className="py-4">
        <Button
          className="btn-primary-custom "
          onClick={() => setShowForm(true)}
        >
          Ajouter Option
        </Button>
        {showForm && (
          <AjoutVille
            onaddVille={addVille}
            onClose={() => setShowForm(false)}
          />
        )}
        <Table bordered hover className="center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom Ville</th>
              <th>Region</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {villes.map((city) => (
              <tr key={city.id}>
                <th scope="row">{city.id}</th>
                <td>{city.name}</td>
                <td>{city.region}</td>
                <td>
                  <Button variant="primary" className="mx-2">
                    View
                  </Button>
                  <Button variant="outline-primary" className="mx-2">
                    Edit
                  </Button>
                  <Button
                    variant="danger"
                    onClick={() => handleDeleteClick(city.id)}
                  >
                    Supprimer
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {showToast && (
          <Toast
            onClose={() => setShowToast(false)}
            show={showToast}
            className="custom-toast-style"
            bg="light"
          >
            <Toast.Body>
              Êtes-vous sûr de vouloir supprimer cette ville ?
              <div className="mt-2 d-flex justify-content-between">
                <Button variant="danger" onClick={confirmDeletion}>
                  Confirmer
                </Button>
                <Button variant="secondary" onClick={() => setShowToast(false)}>
                  Annuler
                </Button>
              </div>
            </Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};

export default Villes;
