  // Example category data with dynamic options
  const categories = [
    {
      id: 1,
      name: "Maison",
      options: ["Climatisé", "chauffé", "vue sur mer"],
    },
    {
      id: 2,
      name: "Appartement",
      options: ["Ascenseur", "Parking", "Balcon"],
    },
    {
      id: 3,
      name: "Studio",
      options: ["Meublé", "Centre ville"],
    },
  ];

  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedCategorieId, setSelectedCategorieId] = useState(null);

