import React, { useState } from "react";
import { Button, Row, Col, Card, Form } from "react-bootstrap";
import Select from "react-select";

const AjoutVille = ({ onAddVille, onClose }) => {
  const [villeName, setVilleName] = useState("");
  const [selectedRegions, setSelectedRegions] = useState(null);
  const [RegionData,setRegionData] =useState([]) 
  const token = useAuth();
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  
  useEffect(() => {
    loadVilles(token);
    console.log(token.token);
    loadRegions(token);
    console.log(token.token);
  }, [token]);


  const loadRegions=()=>{
    axios
    .get("http://localhost:8082/api/region", config.headers)
    .then((response)=>{
      
      setRegionData(response.data)
    })
    .catch((error) => {
      console.error("Error loading regions:", error);
    });
  }

  const submitHandler = (event) => {
    event.preventDefault();
    if (!villeName || !selectedRegions) return;
    onAddVille(villeName, selectedRegions.value);
    setVilleName("");
    setSelectedRegions(null);
    onClose();
  };

  const handleRegionChange = (selectedRegions) => {
    setSelectedRegions(selectedRegions);
  };

  return (
    <Card className="my-4">
      <Card.Body>
        <Form onSubmit={submitHandler}>
          <Form.Group as={Row} controlId="villeName">
            <Form.Label column sm={3}>
              Nom Ville:
            </Form.Label>
            <Col sm={6} className="mb-3">
              {" "}
              {/* mb-3 adds margin bottom */}
              <Form.Control
                type="text"
                placeholder="Entrez le nom de la ville"
                value={villeName}
                onChange={(e) => setVilleName(e.target.value)}
              />
            </Col>
            <Col sm={3} />{" "}
            {/* Colonne vide pour pousser le contenu vers la gauche */}
          </Form.Group>
          <Form.Group as={Row} controlId="regionSelect">
            <Form.Label column sm={3}>
              Region:
            </Form.Label>
            <Col sm={6}>
              <Select
                options={RegionData}
                value={selectedRegions}
                onChange={handleRegionChange}
                className="basic-single"
                classNamePrefix="select"
              />
            </Col>
            <Col sm={3} />{" "}
            {/* Colonne vide pour pousser le contenu vers la gauche */}
          </Form.Group>
          <Row>
            <Col sm={12} className="text-center">
              <Button variant="primary" type="submit" className="m-2">
                Ajouter
              </Button>
              <Button variant="secondary" onClick={onClose} className="m-2">
                Annuler
              </Button>
            </Col>
          </Row>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default AjoutVille;
