import React, { useState } from "react";
import { Table, Button, Container } from "react-bootstrap";

const Regions = () => {
  // Example data
  const [regions,setRegions] =useState ([
    {
      id: 1,
      name: "Sousse",
      cities: ["Sousse City", "Hammam Sousse", "Kalaâ Kebira"],
    },
    {
      id: 2,
      name: "Tunis",
      cities: ["Tunis", "Carthage", "La Marsa"],
    },
  ]);
  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedRegionId, setSelectedRegionId] = useState(null);

  const handleDeleteClick = (regionId) => {
    setSelectedCategorieId(regionId);
    setShowToast(true);
  };

  const confirmDeletion = () => {
    setRegions((currentRegions) =>
    currentRegions.filter(
        (region) => region.id !== selectedRegionId
      )
    );
    setShowToast(false);
    setSelectedRegionId(null);
  };
  const addRegion = (ReName, categories = []) => {  // Assume categories are optionally passed
    const newId = options.length > 0 ? Math.max(...options.map((o) => o.id)) + 1 : 1;
    setOptions([...options, { id: newId, name: optionName, categories }]);
  };

  return (
    <Container>
      <div className="py-4">
        <Table bordered hover>
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom Region</th>
              <th>Villes</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {regions.map(region => (
              <tr key={region.id}>
                <td>{region.id}</td>
                <td>{region.name}</td>
                <td>{region.cities.join(", ")}</td> {/* Display cities as a comma-separated list */}
                <td>
                  <Button variant="primary" className="mx-2">View</Button>
                  <Button variant="outline-primary" className="mx-2">Edit</Button>
                  <Button variant="danger" className="mx-2">Delete</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    </Container>
  );
};


export default Regions;
