import React, { useState } from "react";
import { Button, Row, Col, Card, Form } from "react-bootstrap";
import Select from "react-select";

const AjoutVille = ({ onAddVille, onClose }) => {
  const [villeName, setVilleName] = useState("");
  const [selectedRegion, setSelectedRegion] = useState(null); // Changed to handle single selection

  const regions = [
    { value: "Sousse", label: "Sousse" },
    { value: "Tunis", label: "Tunis" },
    { value: "Gafsa", label: "Gafsa" },
    { value: "Sfax", label: "Sfax" },
  ];

  const submitHandler = (event) => {
    event.preventDefault();
    if (!villeName || !selectedRegion) return; // Ensure a region is selected
    onAddVille(villeName, selectedRegion.value); // Pass the selected region's value
    setVilleName("");
    setSelectedRegion(null);
    onClose();
  };

  const handleRegionChange = (selectedOption) => {
    setSelectedRegion(selectedOption); // Handle single selection
  };

  return (
    <Card className="my-4">
      <Card.Body>
        <Form onSubmit={submitHandler}>
          <Form.Group as={Row} controlId="villeName">
            <Form.Label column sm={3}>Nom de la Ville:</Form.Label>
            <Col sm={6}>
              <Form.Control
                type="text"
                placeholder="Entrez le nom de la ville"
                value={villeName}
                onChange={(e) => setVilleName(e.target.value)}
              />
            </Col>
          </Form.Group>
          <Form.Group as={Row} controlId="regionSelect">
            <Form.Label column sm={3}>RÃ©gion:</Form.Label>
            <Col sm={6}>
              <Select
                options={regions}
                value={selectedRegion}
                onChange={handleRegionChange}
                className="basic-single"
                classNamePrefix="select"
              />
            </Col>
          </Form.Group>
          <Row className="text-center">
            <Col sm={12}>
              <Button variant="primary" type="submit" className="m-2">
                Ajouter
              </Button>
              <Button variant="secondary" onClick={onClose} className="m-2">
                Annuler
              </Button>
            </Col>
          </Row>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default AjoutVille;
