import React, { useState } from "react";
import { Table, Button, Container } from "react-bootstrap";

const Regions = () => {
  // Example data
  const [regions,setRegions] =useState ([
    {
      id: 1,
      name: "Sousse",
      villes: ["Sousse City", "Hammam Sousse", "Kalaâ Kebira"],
    },
    {
      id: 2,
      name: "Tunis",
      villes: ["Tunis", "Carthage", "La Marsa"],
    },
  ]);
  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedRegionId, setSelectedRegionId] = useState(null);

  const handleDeleteClick = (regionId) => {
    setSelectedCategorieId(regionId);
    setShowToast(true);
  };

  const confirmDeletion = () => {
    setRegions((currentRegions) =>
    currentRegions.filter(
        (region) => region.id !== selectedRegionId
      )
    );
    setShowToast(false);
    setSelectedRegionId(null);
  };
  const addRegion = (regionName, villes = []) => {  // Assume categories are optionally passed
    const newId = regions.length > 0 ? Math.max(...regions.map((r) => r.id)) + 1 : 1;
    setRegions([...regions, { id: newId, name: regionName, villes }]);
  };

  return (
    <Container>
      <div className="py-4">
        <Button className="btn-primary-custom " onClick={() => setShowForm(true)}>Ajouter Region</Button>
        {showForm && (
          <AjoutRe
            onAddOption={addOption}
            onClose={() => setShowForm(false)}
          />
        )}
        <Table bordered hover className="custom-table text-center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom de l'option</th>
              <th>Catégories Associées</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {options.map((option) => (
              <tr key={option.id}>
                <td>{option.id}</td>
                <td>{option.name}</td>
                <td>
                  <ul>
                    {option.categories.map((category, index) => (
                      <li key={index}>{category}</li>
                    ))}
                  </ul>
                </td>
                <td className="align-middle">
                  <Button
                    variant="danger"
                    onClick={() => handleDeleteClick(option.id)}
                  >
                    Supprimer
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {showToast && (
          <Toast
            onClose={() => setShowToast(false)}
            show={showToast}
            className="custom-toast-style"
            bg="light"
          >
            <Toast.Body>
              Êtes-vous sûr de vouloir supprimer cette option ?
              <div className="mt-2 d-flex justify-content-between">
                <Button variant="danger" onClick={confirmDeletion}>
                  Confirmer
                </Button>
                <Button
                  variant="secondary"
                  onClick={() => setShowToast(false)}
                >
                  Annuler
                </Button>
              </div>
            </Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};


export default Regions;
