const AjoutOffre = ({ onAddOffer, onClose }) => {
    const [offerName, setOfferName] = useState("");
  
    const submitHandler = (event) => {
      event.preventDefault();
      if (!offerName) return;
      onAddOffer(offerName);  // Envoyez seulement le nom de l'offre
      setOfferName("");
      onClose(); // Fermer le formulaire après soumission
    };
  
    return (
      <form onSubmit={submitHandler}>
        <div>
          <label htmlFor="offerName">Nom de l'offre :</label>
          <input
            id="offerName"
            type="text"
            value={offerName}
            onChange={(e) => setOfferName(e.target.value)}
          />
        </div>
        <button type="submit">Ajouter</button>
        <button type="button" onClick={onClose}>Annuler</button>
      </form>
    );
  };
  
  export default AjoutOffre;
