import React, { useState } from "react";
import { Button, Row, Col, Card, Form } from "react-bootstrap";
import Select from "react-select";

const AjoutRegion = ({ onAddRegion, onClose }) => {
  const [regionName, setRegionName] = useState("");
  const [selectedVilles, setSelectedVilles] = useState([]);

  const villes = [
    { value: "Hammam Sousse", label: "Maison" },
    { value: "Akouda", label: "Appartement" },
    { value: "Studio", label: "Studio" },
    
  ];

  const submitHandler = (event) => {
    event.preventDefault();
    if (!regionName || selectedVilles.length === 0) return;
    onAddRegion(
      regionName,
      selectedVilles.map((villes) => villes.value)
    );
    setRegionName("");
    setSelectedVilles([]);
    onClose();
  };

  const handleVilleChange = (selectedRegions) => {
    setSelectedVilles(selectedRegions || []);
  };

  return (
    <Card className="my-4">
      <Card.Body>
        <Form onSubmit={submitHandler}>
          <Form.Group as={Row} controlId="regionName">
            <Form.Label column sm={3}>
              Nom de Region:
            </Form.Label>
            <Col sm={6} className="mb-3">
              {" "}
              {/* mb-3 adds margin bottom */}
              <Form.Control
                type="text"
                placeholder="Entrez le nom de l'option"
                value={regionName}
                onChange={(e) => setReName(e.target.value)}
              />
            </Col>
            <Col sm={3} />{" "}
            {/* Colonne vide pour pousser le contenu vers la gauche */}
          </Form.Group>
          <Form.Group as={Row} controlId="villesSelect">
            <Form.Label column sm={3}>
              Villes Associ√©es:
            </Form.Label>
            <Col sm={6}>
              <Select
                isMulti
                options={villes}
                value={selectedVilles}
                onChange={handleVilleChange}
                className="basic-multi-select"
                classNamePrefix="select"
              />
            </Col>
            <Col sm={3} />{" "}
            {/* Colonne vide pour pousser le contenu vers la gauche */}
          </Form.Group>
          <Row>
            <Col sm={12} className="text-center">
              <Button variant="primary" type="submit" className="m-2">
                Ajouter
              </Button>
              <Button variant="secondary" onClick={onClose} className="m-2">
                Annuler
              </Button>
            </Col>
          </Row>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default AjoutRegion;
