import React, { useEffect, useState } from "react";
import { Button, Row, Col, Card, Form } from "react-bootstrap";
import axios from "axios";
import { useAuth } from "../../provider/authProvider";

const AjoutVille = ({ onAddVille, onClose }) => {
  const [villeName, setVilleName] = useState("");
  const [selectedRegion, setSelectedRegion] = useState(null);
  const [regionData, setRegionData] = useState([]);
  const token = useAuth();

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  useEffect(() => {
    console.log(token.token);
    loadRegions();
  }, [token]);

  const loadRegions = () => {
    axios
      .get("http://localhost:8082/api/region", config)
      .then((response) => {
        setRegionData(response.data);
      })
      .catch((error) => {
        console.error("Error loading regions:", error);
      });
  };

  const submitHandler = (event) => {
    event.preventDefault();
    if (!villeName || !selectedRegion) return;

    const villeData = {
      nomVille: villeName,
      region_id: selectedRegion,
    };

    axios
      .post("http://localhost:8082/api/ville", villeData, config)
      .then((response) => {
        onAddVille(response.data);
        setVilleName("");
        setSelectedRegion(null);
        onClose();
      })
      .catch((error) => {
        console.error("Error adding ville:", error);
      });
  };

  const handleRegionChange = (event) => {
    setSelectedRegion(event.target.value);
  };

  return (
    <Card className="my-4">
      <Card.Body>
        <Form onSubmit={submitHandler}>
          <Form.Group as={Row} controlId="villeName">
            <Form.Label column sm={3}>
              Nom Ville:
            </Form.Label>
            <Col sm={6} className="mb-3">
              <Form.Control
                type="text"
                placeholder="Entrez le nom de la ville"
                value={villeName}
                onChange={(e) => setVilleName(e.target.value)}
              />
            </Col>
            <Col sm={3} />
          </Form.Group>
          <Form.Group as={Row} controlId="regionSelect">
            <Form.Label column sm={3}>
              Région:
            </Form.Label>
            <Col sm={6}>
              <Form.Select 
                aria-label="Sélectionnez une région"
                value={selectedRegion}
                onChange={handleRegionChange}
              >
                <option value="">Sélectionnez une région</option>
                {regionData.map((region) => (
                  <option key={region.id} value={region.id}>
                    {region.nomRegion}
                  </option>
                ))}
              </Form.Select>
            </Col>
            <Col sm={3} />
          </Form.Group>
          <Row>
            <Col sm={12} className="text-center">
              <Button variant="primary" type="submit" className="m-2">
                Ajouter
              </Button>
              <Button variant="secondary" onClick={onClose} className="m-2">
                Annuler
              </Button>
            </Col>
          </Row>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default AjoutVille;
