import React, { useState } from "react";
import { Button, Container, Toast, Table } from "react-bootstrap";
import AjoutOption from "./AjoutOption";
import "./options.css";

const Options = () => {
  // ... votre état et fonctions 

  return (
    <Container>
      <div className="py-4">
        <Button onClick={() => setShowForm(true)}>Ajouter Option</Button>
        {showForm && (
          <AjoutOption
            onAddOption={addOption}
            onClose={() => setShowForm(false)}
          />
        )}
        <Table bordered hover className="custom-table text-center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom de l'option</th>
              <th>Catégories Associées</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {options.map((option) => (
              <tr key={option.id}>
                <td>{option.id}</td>
                <td>{option.name}</td>
                <td>
                  <ul>
                    {option.categories.map((category, index) => (
                      <li key={index}>{category}</li>
                    ))}
                  </ul>
                </td>
                <td className="action-cell">
                  <Button
                    variant="danger"
                    className="mx-2"
                    onClick={() => handleDeleteClick(option.id)}
                  >
                    Supprimer
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {showToast && (
          <Toast
            onClose={() => setShowToast(false)}
            show={showToast}
            className="custom-toast-style"
            bg="light"
          >
            <Toast.Body>
              Êtes-vous sûr de vouloir supprimer cette option ?
              <div className="mt-2 d-flex justify-content-between">
                <Button variant="danger" size="sm" onClick={confirmDeletion}>
                  Confirmer
                </Button>
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={() => setShowToast(false)}
                >
                  Annuler
                </Button>
              </div>
            </Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};

export default Options;
