import React, { useState } from "react";
import { Button, Container, Toast, Table } from "react-bootstrap";
import AjoutVille from "./AjoutVille";
import './ville.css'
const Villes = () => {
 
  // Sample static data, replace with dynamic data fetching if necessary
  const [villes, setVilles] = useState([
    {
      id: 1,
      name: "Hammam Sousse",
      region: "Sousse",
    },
    {
      id: 2,
      name: "La Marsa",
      region: "Tunis",
    },
    {
      id: 3,
      name: "Sfax",
      region: "Sfax",
    },
  ]);
  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedVilleId, setselectedVilleId] = useState(null);
  const handleDeleteClick = (villeId) => {
    setselectedVilleId(villeId);
    setShowToast(true);
  };

  const confirmDeletion = () => {
    setVilles((currentvilles) =>
      currentvilles.filter((ville) => ville.id !== selectedVilleId)
    );
    setShowToast(false);
    setselectedVilleId(null);
  };

  const addVille = (villeName, region = []) => {
    // Assume categories are optionally passed
    const newId =
      villes.length > 0 ? Math.max(...villes.map((o) => o.id)) + 1 : 1;
    setVilles([...villes, { id: newId, name: villeName, region }]);
  };

  return (
    <Container>
      <div className="py-4">
        <Button className="btn-primary-custom " onClick={() => setShowForm(true)}>Ajouter Ville</Button>
        {showForm && (
          <AjoutVille
            onAddOption={addVille}
            onClose={() => setShowForm(false)}
          />
        )}
        <Table bordered hover className="custom-table text-center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom de Ville</th>
              <th>Region</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {villes.map((ville) => (
              <tr key={ville.id}>
                <td>{ville.id}</td>
                <td>{ville.name}</td>
                <td>
                  <ul>
                    {option.categories.map((category, index) => (
                      <li key={index}>{category}</li>
                    ))}
                  </ul>
                </td>
                <td className="align-middle">
                  <Button
                    variant="danger"
                    onClick={() => handleDeleteClick(option.id)}
                  >
                    Supprimer
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {showToast && (
          <Toast
            onClose={() => setShowToast(false)}
            show={showToast}
            className="custom-toast-style"
            bg="light"
          >
            <Toast.Body>
              Êtes-vous sûr de vouloir supprimer cette option ?
              <div className="mt-2 d-flex justify-content-between">
                <Button variant="danger" onClick={confirmDeletion}>
                  Confirmer
                </Button>
                <Button
                  variant="secondary"
                  onClick={() => setShowToast(false)}
                >
                  Annuler
                </Button>
              </div>
            </Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};

export default Villes;
