import React, { useState } from 'react';
import { Button, Form, FormControl, Row, Col } from 'react-bootstrap';

const AjoutOffre = ({ onAddOffer, onClose }) => {
    const [offerName, setOfferName] = useState("");

    const submitHandler = (event) => {
        event.preventDefault();
        if (!offerName) return;
        onAddOffer(offerName);
        setOfferName("");
        onClose();
    };

    return (
        <Card className="my-4 p-3">
        <Card.Body>
        <Form onSubmit={submitHandler} className="my-4">
            <Row>
                <Col sm={12} md={8}>
                    <Form.Group controlId="offerName">
                        <Form.Label>Nom de l'offre :</Form.Label>
                        <FormControl
                            type="text"
                            placeholder="Entrez le nom de l'offre"
                            value={offerName}
                            onChange={(e) => setOfferName(e.target.value)}
                        />
                    </Form.Group>
                </Col>
                <Col sm={12} md={4} className="mt-3 mt-md-0">
                    <Button variant="primary" type="submit" className="me-2">
                        Ajouter
                    </Button>
                    <Button variant="secondary" onClick={onClose}>
                        Annuler
                    </Button>
                </Col>
            </Row>
        </Form>
        </Card.Body>
    </Card>
    );
};

export default AjoutOffre;
