import React, { useState } from "react";
import { Button, Form, FormControl, Row, Col, Card } from "react-bootstrap";

const AjoutOption = ({ onAddOption, onClose }) => {
  const [optionName, setOptionName] = useState("");
  const [selectedCategories, setSelectedCategories] = useState([]);

  // Supposons que vous avez ces catégories disponibles
  const categories = ["Maison", "Appartement", "Studio", "Bureau", "Villa"];

  const submitHandler = (event) => {
    event.preventDefault();
    if (!optionName || selectedCategories.length === 0) return;
    onAddOption(optionName, selectedCategories);
    setOptionName("");
    setSelectedCategories([]);
    onClose();
  };

  const handleCategoryChange = (event) => {
    const values = Array.from(event.target.selectedOptions, (option) => option.value);
    setSelectedCategories(values);
  };

  return (
    <Card className="my-4">
      <Card.Body>
        <Form onSubmit={submitHandler}>
          <Row>
            <Col xs={12} md={6}>
              <Form.Group controlId="optionName">
                <Form.Label className="fw-bold">Nom de l'option:</Form.Label>
                <FormControl
                  type="text"
                  placeholder="Entrez le nom de l'option"
                  value={optionName}
                  onChange={(e) => setOptionName(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col xs={12} md={6}>
              <Form.Group controlId="categorySelect">
                <Form.Label>Catégories Associées</Form.Label>
                <FormControl
                  as="select"
                  multiple
                  value={selectedCategories}
                  onChange={handleCategoryChange}
                >
                  {categories.map((category, index) => (
                    <option key={index} value={category}>
                      {category}
                    </option>
                  ))}
                </FormControl>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col xs={6} md={3}>
              <Button variant="primary" type="submit" className="w-100">
                Ajouter
              </Button>
            </Col>
            <Col xs={6} md={3}>
              <Button variant="secondary" onClick={onClose} className="w-100">
                Annuler
              </Button>
            </Col>
          </Row>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default AjoutOption;
