import React, { useState } from "react";
import {
  Button,
  Container,
  Toast,
  Table,
  Form,
  FormControl,
  Row,
  Col,
  Card,
} from "react-bootstrap";
import AjoutOption from "./AjoutOption";
import "./options.css";
const Options = () => {
  // Votre état initial et les fonctions
  // ...
  const [options, setOptions] = useState([
    {
      id: 1,
      name: "Climatisé",
      categories: ["Maison", "Appartement", "Studio"],
    },
    {
      id: 2,
      name: "chauffé",
      categories: ["Maison", "Bureau"],
    },
    {
      id: 3,
      name: "vue sur mer",
      categories: ["Maison", "Villa"],
    },
  ]);

  const [showForm, setShowForm] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [selectedOptionId, setSelectedOptionId] = useState(null);

  const handleDeleteClick = (optionId) => {
    setSelectedOptionId(optionId);
    setShowToast(true);
  };

  // Logique pour supprimer une option
  const confirmDeletion = () => {
    setOptions((options) =>
      options.filter((option) => option.id !== selectedOptionId)
    );
    setShowToast(false);
    setSelectedOptionId(null);
  };

  const addOption = (optionName) => {
    // Trouvez le plus grand ID actuel et ajoutez 1 pour le nouvel ID
    const newId =
      options.length > 0
        ? Math.max(...options.map((option) => option.id)) + 1
        : 1;
    const newOption = {
      id: newId,
      name: optionName,
    };
    setOptions((prevOptions) => [...prevOptions, newOption]);
  };

  return (
    <Container>
      <div className="py-4">
        <Button
          className="btn-primary-custom "
          onClick={() => setShowForm(true)}
        >
          Ajouter Option
        </Button>
        {showForm && (
          <AjoutOption // Utilisez ici le nouveau nom du composant
            onAddOption={addOption}
            onClose={() => setShowForm(false)}
          />
        )}
        <Table bordered hover className="custom-table text-center">
          <thead>
            <tr>
              <th>Id</th>
              <th>Nom de l'option</th>
              <th>Categories Associées</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {options.map((option) => (
              <tr key={option.id}>
                <th scope="row">{option.id}</th>
                <td>{option.name}</td>
                <td>
                  <Button
                    variant="danger"
                    className="mx-2"
                    onClick={() => handleDeleteClick(option.id)}
                  >
                    Supprimer
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {showToast && (
          <Toast
            onClose={() => setShowToast(false)}
            show={showToast}
            className="custom-toast-style"
            bg="light"
          >
            <Toast.Body>
              Êtes-vous sûr de vouloir supprimer cette option ?
              <div className="mt-2 d-flex justify-content-between">
                <Button variant="danger" size="sm" onClick={confirmDeletion}>
                  Confirmer
                </Button>
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={() => setShowToast(false)}
                >
                  Annuler
                </Button>
              </div>
            </Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};

export default Options;
